CREATE FUNCTION [dbo].[GetReqID]()
RETURNS varchar(20)
AS
BEGIN
  DECLARE @ReqID varchar(20)
  DECLARE @DT nvarchar(20)
  SELECT @DT = convert(varchar(10),getdate(),112)
  SELECT @ReqID= 'R' + @DT + right('0000' + ltrim(isnull(max(cast(right(req_id, 5) as int)),0)+1), 5) from REQUISITION where left(req_id, 9) = 'R' + @DT
  RETURN @ReqID
END
