CREATE FUNCTION [dbo].[GetShipID]()
RETURNS varchar(20)
AS
BEGIN
  DECLARE @ShipID varchar(20)
  DECLARE @DT nvarchar(20)
  SELECT @DT = convert(varchar(10),getdate(),112)
  SELECT @ShipID= 'S' + @DT + right('0000' + ltrim(isnull(max(cast(right(ship_id, 5) as int)),0)+1), 5) from SHIPMENTS where left(ship_id, 9) = 'S' + @DT
  RETURN @ShipID
END
